package aoc2022

import scala.annotation.tailrec

//noinspection SpellCheckingInspection
object Day6 {

  private val part1Sample1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
  private val part1Sample2 = "bvwbjplbgvbhsrlpgdmjqwftvncz"
  private val part1Sample3 = "nppdvjthqldpwncqszvftbrmjlhg"
  private val part1Sample4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"
  private val part1Sample5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
  private val part1Input =
    ""

  @tailrec
  private def getIndexAfterUniqueChars(
      input: String,
      numberOfUniqueChars: Int,
      index: Int = 0
  ): Int = {
    val checkChars = input.slice(index, index + numberOfUniqueChars)
    if (checkChars.distinct.length == checkChars.length)
      index + numberOfUniqueChars
    else
      getIndexAfterUniqueChars(input, numberOfUniqueChars, index + 1)
  }
  private def getPacketStartIndex(input: String): Int = {
    getIndexAfterUniqueChars(input, 4)
  }

  private def getMessageStartIndex(input: String): Int = {
    getIndexAfterUniqueChars(input, 14)
  }

  def main(args: Array[String]): Unit = {
    println(getPacketStartIndex(part1Sample1))
    println(getPacketStartIndex(part1Sample2))
    println(getPacketStartIndex(part1Sample3))
    println(getPacketStartIndex(part1Sample4))
    println(getPacketStartIndex(part1Sample5))
    println(getPacketStartIndex(part1Input))

    println(getMessageStartIndex(part1Sample1))
    println(getMessageStartIndex(part1Sample2))
    println(getMessageStartIndex(part1Sample3))
    println(getMessageStartIndex(part1Sample4))
    println(getMessageStartIndex(part1Sample5))
    println(getMessageStartIndex(part1Input))
  }
}
